import React from 'react';
import ThemeContext from '../context/ThemeContext';
const Switch = () => {
	const { dark, toggle } = React.useContext(ThemeContext);
	console.log(dark);
	return (
		<button
			style={{ marginTop: 10 }}
			onClick={() => {
				toggle();
			}}
		>
			Dark mode
		</button>
	);
};
export default Switch;
