import React, { useState } from 'react';
import { Row, Col, Form, Input, Button } from 'antd';
import { useHistory } from 'react-router-dom';
import { api } from '../../services/api';
import { helper } from '../../helpers/common';
import { Checkbox } from 'antd';
const LoginMovie = () => {
  const history = useHistory();
  const [errorLogin, setErrorLogin] = useState(null);
  const [rememberme, setRememberme] = useState(false);

  const onFinish = values => {
    // admin - 123
    const { username, password } = values;
    const token = api.checkLoginUser(username, password);
    if (token !== null) {
      setErrorLogin(null);
      // luu token vao localStorage - cookie browser
      helper.saveTokenLocalStorage(token);
      // quay vao trang tim kiem phim
      history.push('/search-movie');
    } else {
      setErrorLogin('account invalid');
    }
  };

  const Username = window.localStorage.getItem('Username');
  const Password = window.localStorage.getItem('Savepassword');
  window.localStorage.setItem('rememberme', rememberme);
  // const [memberme, setRememberme] = useState({
  //   username: Username,
  //   password: Password
  // });
  const Rememberme = window.localStorage.getItem('rememberme');
  const onFinishFailed = errorInfo => {
    console.log('Failed:', errorInfo);
  };

  return (
    <Row>
      <Col span={12} offset={6}>
        <h1 style={{ textAlign: 'center' }}> Login !</h1>
        {errorLogin !== null && (
          <p style={{ color: 'red', textAlign: 'center' }}> {errorLogin} </p>
        )}
        <Form
          name="basic"
          labelCol={{
            span: 8
          }}
          wrapperCol={{
            span: 16
          }}
          initialValues={{
            remember: true,
            username: Username,
            password: Password
          }}
          onFinish={onFinish}
          onFinishFailed={onFinishFailed}
        >
          <Form.Item
            label="Username"
            name="username"
            rules={[
              {
                required: true,
                message: 'Please input your username!'
              }
            ]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            label="Password"
            name="password"
            rules={[
              {
                required: true,
                message: 'Please input your password!'
              }
            ]}
          >
            <Input.Password />
          </Form.Item>

          <Form.Item
            wrapperCol={{
              offset: 8,
              span: 16
            }}
          >
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <Button type="primary" htmlType="submit">
                Submit
              </Button>
              <Checkbox
                onChange={e =>
                  // setRememberme(e.target.checked)
                  window.localStorage.setItem('rememberme', e.target.checked)
                }
                // defaultChecked={true}
              >
                Remember me
              </Checkbox>
            </div>
          </Form.Item>
        </Form>
      </Col>
    </Row>
  );
};
export default React.memo(LoginMovie);
