import React from 'react';
import ContextTheme from './ContextTheme';
// const Themelight = ['--color: #2c3e50', '--background-color: #fff'];
// const Themedark = ['--color: #fff', '--background-color: #2c3e50'];
class ContextProvider extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			dark: false // luu thong tin data tu api
		};
	}
	Applytheme = theme => {
		const root = document.getElementsByTagName('html')[0];
		root.style.cssText = theme.join(';');
	};

	ToggleButton = () => {
		this.setState(state => ({ ...state, dark: !state.dark }));
	};

	render() {
		return (
			<ContextTheme.Provider value={{ dark: this.state.dark, toggle: this.ToggleButton }}>
				{this.props.children}
			</ContextTheme.Provider>
		);
	}
}
export default ContextProvider;
