import React, { Component } from 'react';
import ThemeContext from './ThemeContext';
import PropTypes from 'prop-types';
const themelight = ['--background-color: white'];
const themedark = ['--background-color: black'];
class ThemeProvider extends Component {
	constructor(props) {
		super(props);

		this.state = {
			dark: false
		};
	}

	applytheme = theme => {
		const root = document.getElementsByTagName('html')[0];
		root.style.cssText = theme.join(';');
	};

	toggle = () => {
		this.setState({ ...this.state, dark: !this.state.dark });
		window.localStorage.setItem('Darktheme', !this.state.dark);
	};
	componentDidMount() {
		const lasttheme = window.localStorage.getItem('Darktheme');
		if (lasttheme === true) {
			this.setState({ ...this.state, dark: true });
		} else {
			this.setState({ ...this.state, dark: false });
		}
	}

	componentDidUpdate() {
		const theme = window.localStorage.getItem('Darktheme');

		if (theme === false) {
			this.applytheme(themelight);
		} else {
			this.applytheme(themedark);
		}
	}

	render() {
		return (
			<ThemeContext.Provider value={{ dark: this.state.dark, toggle: this.toggle }}>
				{this.props.children}
			</ThemeContext.Provider>
		);
	}
}
ThemeProvider.propTypes = {
	children: PropTypes.node.isRequired
};

export default ThemeProvider;
