import React from 'react';
import './styles.css';
import { BiMoon } from 'react-icons/bi';
import { FaRegLightbulb } from 'react-icons/fa';
import contextTheme from '../context/ContextTheme';
const Toggle = () => {
	const { dark } = React.useContext(contextTheme);
	console.log(dark);
	return (
		<>
			<h3>Switch theme</h3>
			<button
				onClick={() => {
					toggle();
				}}
			>
				<BiMoon style={{ fontSize: 20 }} />
				<FaRegLightbulb style={{ fontSize: 20, marginLeft: 12 }} />
			</button>
		</>
	);
};

export default Toggle;
