import React, { Component } from 'react';
import ContextTheme from './ContextTheme';
export default class contextProvider extends Component {
	constructor(props) {
		super(props);
		this.state = {
			dark: false
		};
	}

	toggle = () => {
		this.setState(state => ({ ...state, dark: !state.dark }));
	};

	render() {
		return <ContextTheme.Provider value={{ dark: this.state.dark }}>{this.props.children}</ContextTheme.Provider>;
	}
}
