import React from 'react';
import { FaPlusSquare } from 'react-icons/fa';
const form = ({ setInputhandler, Inputhandler, Submitdata, setSubmitdata, setDropdowntodo, Dropdowntodo }) => {
	const HandlerInput = e => {
		setInputhandler(e.target.value);
	};
	const Confirmedata = e => {
		e.preventDefault();
		if (Inputhandler !== '') {
			setSubmitdata([...Submitdata, { data: Inputhandler, completed: false, id: Math.random() * 1000 }]);
		}

		setInputhandler('');
	};

	const Dropdownmenu = e => {
		setDropdowntodo(e.target.value);
		switch (Dropdowntodo) {
			case 'completed':
				setSubmitdata(Submitdata.filter(e => e.completed === 'true'));
				break;
			case 'uncompleted':
				setSubmitdata(Submitdata.filter(e => e.completed === 'false'));
				break;

			default:
				break;
		}
	};
	console.log(Submitdata);
	return (
		<>
			<div className="Formparent">
				<div className="input_parent">
					<input
						value={Inputhandler}
						onChange={e => HandlerInput(e)}
						type="text"
						className="input_todo"
					></input>
					{/* <div className="todo_plus"></div> */}

					<FaPlusSquare className="plus" onClick={Confirmedata} />
				</div>
				<select name="cars" id="Dropdown_todo" onChange={Dropdownmenu}>
					<option value="All">All</option>
					<option value="completed">completed</option>
					<option value="uncompleted">uncompleted</option>
				</select>
			</div>
		</>
	);
};

export default form;
